// Динамічний генератор manifest.json через JavaScript
function generateDynamicManifest() {
    const urlParams = new URLSearchParams(window.location.search);
    const clientId = urlParams.get('id');
    
    // Валідуємо client ID (UUID формат)
    const isValidUUID = clientId && /^[a-f0-9]{8}-[a-f0-9]{4}-[a-f0-9]{4}-[a-f0-9]{4}-[a-f0-9]{12}$/i.test(clientId);
    
    // Отримуємо базовий URL
    const baseUrl = window.location.origin;
    const startUrl = isValidUUID ? `${baseUrl}/index.html?id=${encodeURIComponent(clientId)}` : `${baseUrl}/`;
    
    const manifest = {
        "name": "АГРО-ПРОСТІР",
        "short_name": "Агро-простір",
        "description": "Інтерактивна платформа для аграрного сектору України",
        "start_url": startUrl,
        "display": "standalone",
        "orientation": "any",
        "theme_color": "#198754",
        "background_color": "#ffffff",
        "scope": "/",
        "lang": "uk",
        "prefer_related_applications": false,
        "edge_side_panel": {
            "preferred_width": 480
        },
        "protocol_handlers": [],
        "launch_handler": {
            "client_mode": "navigate-existing"
        },
        "icons": [
            {
                "src": "data:image/svg+xml;base64,",
                "sizes": "192x192",
                "type": "image/svg+xml",
                "purpose": "maskable any"
            },
            {
                "src": "data:image/svg+xml;base64,",
                "sizes": "512x512",
                "type": "image/svg+xml",
                "purpose": "maskable any"
            },
            {
                "src": "data:image/svg+xml;base64,",
                "sizes": "144x144",
                "type": "image/svg+xml",
                "purpose": "any"
            },
            {
                "src": "data:image/svg+xml;base64,",
                "sizes": "96x96",
                "type": "image/svg+xml",
                "purpose": "any"
            },
            {
                "src": "data:image/svg+xml;base64,",
                "sizes": "72x72",
                "type": "image/svg+xml",
                "purpose": "any"
            },
            {
                "src": "data:image/svg+xml;base64,",
                "sizes": "48x48",
                "type": "image/svg+xml",
                "purpose": "any"
            },
            {
                "src": "data:image/svg+xml;base64,",
                "sizes": "36x36",
                "type": "image/svg+xml",
                "purpose": "any"
            }
        ],
        "categories": ["business", "productivity", "utilities"]
    };
    
    return manifest;
}

// Експортуємо функцію для використання
if (typeof module !== 'undefined' && module.exports) {
    module.exports = { generateDynamicManifest };
}
